//예약완료
import React, { useState, useEffect } from "react";
import axios from "axios";
import 'bootstrap/dist/css/bootstrap.min.css';
import './ReservationList.css';
import './watingList.css';
import "@popperjs/core";
import ShopReservationList from "./shopReservationList"; 


const fixedlist = {
  'date':'07.04. 13:00',
  'shop':'봉봉살롱',
  'address':'부산광역시',
  'telephone':'010-0000-0000'
}

function FixedList() {
  const [showDetails, setShowDetails] = useState(false);
  const [data, setData] = useState({});
  const [fetchedData, setFetchedData] = useState(false); // 상태 추가

  useEffect(() => {
    if (!fetchedData) {
      // fetchedData 상태가 false일 때만 요청 보냄 (새로고침 할때)
      const token = 'eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzUxMiJ9.eyJzdWIiOiJjb3PthqDtgbAiLCJpZCI6NCwiZXhwIjoxNjkyNzYxMDA0LCJ1c2VybmFtZSI6Imdvb2dsZV8xMDg1OTYwMzM2NDczMDk5ODQ3ODUifQ.uBYZMFGYe2wq6w3LzO1TPdmg6evnMtEZGQHmSszo8yaqUtGeraBjeA-YQepR5pQn1Mi_IqkMWPOFGdMTI47EFA'; // 토큰 추가

      axios({
        method: 'GET',
        url: '/manage/bookings/confirmed',
        headers: {
          Authorization: `Bearer ${token}`
        }
      })
        .then(response => {
          setData(response.data);
          setShowDetails(true);
          setFetchedData(true); // fetchedData 상태를 true로 변경
          console.log('성공:', response.data);
        })
        .catch(error => {
          console.error('에러 발생:', error);
        });
    }
  }, [fetchedData]);
  const handleDetailsClick = () => {
      setShowDetails(prevShowDetails => !prevShowDetails);
  };

  return (
      <div className="wationgBox">
          <div className="waiting">예약완료</div>
          <div className="rowline"></div>  
          <div className="colPage2">
              <p className="tile title1">예약일자</p>
              <p className="tile">반려동물이름</p>
              <p className="tile">보호자 이름</p>
              <p className="tile">보호자 연락처</p>
              <p className="tile title5">예약상세</p>
          </div>
          <div>
              <ShopReservationList 
                  date={fixedlist.date}
                  shop={fixedlist.shop}
                  address={fixedlist.address}
                  telephone={fixedlist.telephone}
              />
          </div> 
      </div>           
  );
}

export default FixedList;


