// 예약상세3
import React,{useState} from 'react';
import FinalPage from './finalPage';
import DateSelct from './dateSelect'
import { faCalendarDays  } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faClock  } from "@fortawesome/free-solid-svg-icons";
import './selectPage.css';
import TimeSelect from './timeSelect';
import './timeSelect.css'

function SelectPage() {
  const [showFinalPage, setShowFinalPage] = useState(false);

  const handleRequestClick = () => {
    setShowFinalPage(true);
  };

  if (showFinalPage) {
    return <FinalPage />;
  }
  
  const handleShwoDetails = () => {

    // 데이터 요청
    const token = 'eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzUxMiJ9.eyJzdWIiOiJjb3PthqDtgbAiLCJpZCI6MiwiZXhwIjoxNjkyNzg1ODEzLCJ1c2VybmFtZSI6Im5hdmVyX1BPbXlOMlNCSndaUmNXbXNUak05YWR6WnNrQ1Qta1Jxd0lick1STHI2LWsifQ.ndm6Q9GkjJZiwkXZae8VV5QDP7ydWp7YBN-ECVyQDWBe0OTwbecCkA11ebrqrrgEw-zqK1p0JHl16F1yz8Pu-g';
    axios({
        method: 'POST',
        url: '/booking/time',
        headers: {
            Authorization: `Bearer ${token}` // Bearer 토큰을 "Authorization" 헤더에 추가
        }
    })
        .then(response => {
            setData(response.data);
            setShowDetails(true);
            console.log('성공:', response.data);

        })
        .catch(error => {
            console.error('에러 발생:', error);
        });
        
};
    return (
      <div className='all'>
        <div className='main_S'>
          <div className='left'>
            <div className='top'>
              <FontAwesomeIcon icon={faCalendarDays} className='icon' />
              날짜선택
            </div>
            <DateSelct/>  
          </div>
          <div className='line'></div>
          <div className='right'>
            <div className='top'>
              <FontAwesomeIcon icon={faClock} className='icon' />
              시간선택
            </div>
            <TimeSelect/>
          </div>
        </div>
        <button className='request'onClick={handleRequestClick}>예약요청</button>
      </div>

    );
  }
  export default SelectPage;
