// 예약상세3
import React,{useState} from 'react';
import FinalPage from './finalPage';
import DateSelct from './dateSelect'
import { faCalendarDays  } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faClock  } from "@fortawesome/free-solid-svg-icons";
import './selectPage.css';
import TimeSelect from './timeSelect';
import './timeSelect.css'
import axios from "axios";

function SelectPage() {
  const [selectedDate, setSelectedDate] = useState(""); // 선택한 날짜를 상태로 저장
  const [showFinalPage, setShowFinalPage] = useState(false);
  const [data, setData] = useState({});
  const [isSelected, setIsSelected] = useState(Array(10).fill(false)); // 초기값은 선택되지 않음으로 설정

  const handleDateSelect = (date) => {
    setSelectedDate(date); // 하위 컴포넌트로부터 받은 날짜 값을 상태로 저장
    console.log (date)
  };

  const handleTimeClick = (index) => {
    const updatedSelected = [...isSelected];
    updatedSelected[index] = !updatedSelected[index];
    setIsSelected(updatedSelected);

    const selectedTime = index + 11;
    console.log("Selected Time:", selectedTime); //timeSelect에서 받은값값
  }
  const handleRequestClick = () => {
    setShowFinalPage(true);
    const token = 'eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzUxMiJ9.eyJzdWIiOiJjb3PthqDtgbAiLCJpZCI6MiwiZXhwIjoxNjkyNzg1ODEzLCJ1c2VybmFtZSI6Im5hdmVyX1BPbXlOMlNCSndaUmNXbXNUak05YWR6WnNrQ1Qta1Jxd0lick1STHI2LWsifQ.ndm6Q9GkjJZiwkXZae8VV5QDP7ydWp7YBN-ECVyQDWBe0OTwbecCkA11ebrqrrgEw-zqK1p0JHl16F1yz8Pu-g';
    axios({
        method: 'POST',
        url: '/booking/time/5',  //예약날짜가 정해지면 reservationId가 생성되는거 아닌가?
        headers: {
            Authorization: `Bearer ${token}`, // Bearer 토큰을 "Authorization" 헤더에 추가
            ContentType: 'application/json' 
        },
        data: {
          startTime : '2023-08-01 15:00'
        }

    
    })
        .then(response => {
            setData(response.data);
            console.log('성공:', response.data);

        })
        .catch(error => {
            console.error('에러 발생:', error.response);
        });
  };

  if (showFinalPage) {
    return <FinalPage />;
    
  }
  

    return (
      <div className='all'>
        <div className='main_S'>
          <div className='left'>
            <div className='top'>
              <FontAwesomeIcon icon={faCalendarDays} className='icon' />
              날짜선택
            </div>
            <DateSelct onDateSelect={handleDateSelect}/>  
          </div>
          <div className='line'></div>
          <div className='right'>
            <div className='top'>
              <FontAwesomeIcon icon={faClock} className='icon' />
              시간선택
            </div>
            <TimeSelect isSelected={isSelected} onClick={handleTimeClick}/>
          </div>
        </div>
        <button className='request'onClick={handleRequestClick}>예약요청</button>
      </div>

    );
  }
  export default SelectPage;
