const waitinglist = {
  'date': '07.04. 13:00',
  'shop': '봉봉살롱',
  'address': '부산광역시',
  'telephone': '010-0000-0000'
}

class ReservationList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      showDetails: false
    };
  }

  handleDetailsClick = () => {
    if (this.props.page === 2) {
      this.setState((prevState) => ({
        showDetails: !prevState.showDetails
      }));
    }
  };

  handlePageClose = () => {
    this.setState({
      showDetails: false
    });
  };

  render() {
    const className = this.props.page === 1 ? 'page-one' : 'page-two';
    const { showDetails } = this.state;

    return (
      <div className={`my-component ${className}`}>
        <div className="one">
          <p className="infromation">{this.props.date}</p>
          <p className="infromation">{this.props.shop}</p>
          <p className="infromation">{this.props.address}</p>
          <p className="infromation">{this.props.telephone}</p>
          {this.props.page === 2 && (
            <p className="infromation">
              <button className="inform_btn" onClick={this.handleDetailsClick}>
                {showDetails ? '닫기' : '상세보기'}
              </button>
            </p>
          )}
        </div>
        {showDetails && (
          <div>
            <Table page={this.props.page} />
            {this.props.page === 2 && (
              <div>
                <button className="agree">승인</button>
                <button className="reject">반려</button>
              </div>
            )}
            <button onClick={this.handlePageClose}>닫기</button>
          </div>
        )}
      </div>
    );
  }
}

export default ReservationList;
