import React from "react";
import './ReservationList.css';
import "@popperjs/core";
import 'bootstrap/dist/css/bootstrap.min.css';
import './Reservation.css';
import Table from './table';
import swal from 'sweetalert2';
import './CustomSwalStyle.css';


class ShopReservationList extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
          showDetails: false
        };
      }
      handleDetailsClick = () => {
        this.setState((prevState) => ({
          showDetails: !prevState.showDetails
        }));
      };
        handleButtonClick = () => {
            swal.fire({
                title: ' ',
                text: '예약승인이 완료되었습니다.',
                confirmButtonText: 'X',
                customClass: {
                  container: 'custom-container', // 커스텀 클래스 적용
                  popup: 'custom-popup', // 커스텀 클래스 적용
                  title: 'custom-title', // 커스텀 클래스 적용
                  htmlContainer: 'custom-html-container', // 커스텀 클래스 적용
                  confirmButton: 'custom-confirm-button',
                },
                position: 'center', // 팝업 창을 가운데로 정렬
                buttonsStyling: false,

              });
  };
            handleButtonClick = () => {
                swal.fire({
                    title: ' ',
                    text: '반려사유를 선택해주세요.',
                    html: `
                    <select id="dropdown">
                      <option value="option1">Option 1</option>
                      <option value="option2">Option 2</option>
                      <option value="option3">Option 3</option>
                    </select>
                  `,
                    confirmButtonText: 'X',
                    customClass: {
                    container: 'custom-container', // 커스텀 클래스 적용
                    popup: 'custom-popup', // 커스텀 클래스 적용
                    title: 'custom-title', // 커스텀 클래스 적용
                    htmlContainer: 'custom-html-container', // 커스텀 클래스 적용
                    confirmButton: 'custom-confirm-button',
                    },
                    position: 'center', // 팝업 창을 가운데로 정렬
                    buttonsStyling: false,

                });
            };

    render(){
 
        const className = this.props.page === 1 ? 'page-one' : 'page-two';
        //페이지1=고객 페이지2=업체 즉
        const { showDetails } = this.state;
        return(
            <div className={`my-component ${className}`}>
                <div className="one">
                <p className="infromation">{this.props.date}</p>
                <p className="infromation">{this.props.shop}</p>
                <p className="infromation">{this.props.address}</p>
                <p className="infromation">{this.props.telephone}</p>
                <p className="infromation"><button className="inform_btn"  onClick={this.handleDetailsClick}>{showDetails ? '닫기' : '상세보기'}</button></p>                
                </div>
                {showDetails && (
                <div>
                  <Table/>
                  <div>
                  <button className="agree"onClick={this.handleButtonClick}>승인</button>
                  <button className="reject" onClick={this.handleButtonClick}>반려</button>
                  </div>
                </div>                
      )}
            </div>
            
        )
    
    }
        
}
export default ShopReservationList;
