import React, { useState,useRef, useEffect } from 'react';

function App() {
  const [data, setData] = useState([]);
  const dataId = useRef(0);

  const getData = async () => {
    const res = await fetch(
      "http://animore.co.kr/userInfo"
    ).then((res) => res.json());
    const initData = res.slice(0, 20).map((item) => {
      return {
        author: item.email,
        content: item.body,
        emotion: Math.floor(Math.random() * 5) + 1,
        createdTime: new Date().getTime(),
        id: dataId.current++
      };
    });

    setData(initData);
  };

  useEffect(() => {
    getData();
  }, []);

  console.log("상태 데이터:", data); // 상태 데이터 출력

  const onCreate = (author, content, emotion) => {
    const createdTime = new Date().getTime();
    const newItem = {
      author: author,
      content: content,
      emotion: emotion,
      createdTime,
      id: dataId.current
    };
    dataId.current += 1;
    setData([newItem, ...data]);
  };
}
export default App;
