import React from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import './ReservationList.css';
import "@popperjs/core";
import './Reservation.css';
import Table from './table';

class WaitingList extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
          showDetails: false
        };
      }
    handleDetailsClick = () => {
        this.setState((prevState) => ({
          showDetails: !prevState.showDetails
        }));
      };
    render(){
        const { showDetails } = this.state;
        return(
            <div>
                <div>예약완료</div>
                <div className="rowline"></div>  
                <div className="col_">
                    <p className="tile">예약일자</p>
                    <p className="tile">매장명</p>
                    <p className="tile">매장 주소</p>
                    <p className="tile">매장 연락처</p>
                    <p className="tile">예약상세</p>
                </div> 
                <div className="one">
                <p className="infromation">{this.props.date}</p>
                <p className="infromation">{this.props.shop}</p>
                <p className="infromation">{this.props.address}</p>
                <p className="infromation">{this.props.telephone}</p>
                <p className="infromation"><button className="inform_btn"  onClick={this.handleDetailsClick}>상세보기</button></p>                
                </div>
                {showDetails && (
                <Table/>
      )}
            </div>           
        )
    
    }
        
}
export default WaitingList;
