import React ,{useState}from "react";
import './ReservationList.css';
import "@popperjs/core";
import 'bootstrap/dist/css/bootstrap.min.css';
import './Reservation.css';
import Table from './table';

function ReservationList (){
  const [data, setData] = useState({});
  const [showDetails, setShowDetails] = useState(false);

      handleDetailsClick = () => {
        this.setState((prevState) => ({
          showDetails: !prevState.showDetails
        }));
      };

        const className = props.page === 1 ? 'page-one' : 'page-two';
        //페이지1=고객 페이지2=업체 즉
        return(
            <div className={`my-component ${className}`}>
                <div className="one">
                <p className="infromation">{this.props.date}</p>
                <p className="infromation">{this.props.shop}</p>
                <p className="infromation">{this.props.address}</p>
                <p className="infromation">{this.props.telephone}</p>
                <p className="infromation"><button className="inform_btn"  onClick={setShowDetails}>{showDetails ? '닫기' : '상세보기'}</button></p>                
                </div>
                {showDetails && (
                  <Table page={2}  data={data} showDetails={showDetails}/>
             
      )}
            </div>
            
        )
    
    }
        

export default ReservationList;


