const waitinglist = {
  'date':'07.04. 13:00',
  'shop':'봉봉살롱',
  'address':'부산광역시',
  'telephone':'010-0000-0000'
}

class WaitingList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      showDetails: false
    };
  }

  handleDetailsClose = () => {
    // 페이지 1과 2 닫기 기능
    this.setState({
      showDetails: false
    });
  };

  render() {
    return (
      <div className="waitingBox">
        <div className="waiting">예약요청</div>
        <div className="rowline"></div>  
        <div className="colPage2">
          <p className="tile title1">예약일자</p>
          <p className="tile">반려동물이름</p>
          <p className="tile">보호자 이름</p>
          <p className="tile">보호자 연락처</p>
          <p className="tile title5">예약상세</p>
        </div>
        <div>
          <ReservationList
            page={2}
            date={waitinglist.date}
            shop={waitinglist.shop}
            address={waitinglist.address}
            telephone={waitinglist.telephone}
          />
        </div>
        {this.state.showDetails && (
          <div>
            <Table page={this.props.page} />
            <div>
              <button className="agree">승인</button>
              <button className="reject">반려</button>
              <button onClick={this.handleDetailsClose}>페이지 닫기</button>
            </div>
          </div>
        )}
      </div>
    );
  }      
}

export default WaitingList;
