import { right } from "@popperjs/core";
import React from "react";
import './radio.css';

function Radio({ children, value, name, onChange }) {
  const handleChange = (event) => {
    onChange(event.target.value); // 선택된 값(value)을 상위 컴포넌트로 전달
  };
    return (
      <label style={{width:94.84}}>
        <input className="radiobtn"
          type="radio"
          value={value}
          name={name}
          onChange={handleChange} // input 요소의 값 변경 시 handleChange 함수 호출
        />
        {children}
      </label>
    );
  }

  export default Radio;
