import React ,{useState}from "react";
// import './ReservationList.css';
import "@popperjs/core";
import 'bootstrap/dist/css/bootstrap.min.css';
import './Reservation.css';
import Table from './table';
import swal from 'sweetalert2';
import './CustomSwalStyle.css';
import './shopReservationList.css'
function ShopReservationList(props) {
  const [showDetails, setShowDetails] = useState(false);
  const [rejectionReason, setRejectionReason] = useState("");

  const handleDetailsClick = () => {
    setShowDetails(prevShowDetails => !prevShowDetails);
  };

  const handleApprovalClick = () => {
    swal.fire({
      title: " ",
      text: "예약승인이 완료되었습니다.",
      confirmButtonText: "X",
      customClass: {
        container: "custom-container",
        popup: "custom-popup",
        title: "custom-title",
        htmlContainer: "custom-html-container",
        confirmButton: "custom-confirm-button",
      },
      position: "center",
      buttonsStyling: false,
    });
  };

  const handleRejectionClick = () => {
    swal.fire({
      title: "반려사유를 선택해주세요.",
      html: (
        <div>
          <select id="dropdown">
            <option value="">-- 선택하세요 --</option>
            <option value="option1">임시휴무일로 인해 취소합니다.</option>
            <option value="option2">예약 중복으로인해 취소합니다.</option>
            <option value="직접입력">직접입력</option>
          </select>
          <div id="inputReasonWrapper" style={{ display: "none" }}>
            <input
              type="text"
              id="inputReason"
              placeholder="사유를 입력해주세요."
            />
          </div>
        </div>
      ),
      confirmButtonText: "X",
      customClass: {
        container: "custom-container",
        popup: "custom-popup",
        title: "custom-title",
        htmlContainer: "custom-html-container",
        confirmButton: "custom-confirm-button",
      },
      position: "center",
      buttonsStyling: false,
      preConfirm: () => {
        const selectedValue = document.getElementById("dropdown").value;
        if (selectedValue === "직접입력") {
          const inputReason = document.getElementById("inputReason").value;
          return inputReason;
        } else {
          return selectedValue;
        }
      },
    }).then((result) => {
      if (result.isConfirmed) {
        const reason = result.value;
        setRejectionReason(reason);
        // 반려 처리하는 로직 추가
        console.log("반려 사유:", reason);
      }
    });
  };

  const className = props.page === 1 ? "page-one" : "page-two";

  return (
    <div className={`my-component ${className}`}>
      <div className="one">
        <p className="infromation">{props.startTime}</p>
        <p className="infromation">{props.petName}</p>
        <p className="infromation">{props.username}</p>
        <p className="infromation">{props.phone}</p>
        <p className="infromation">
          <button className="inform_btn" onClick={handleDetailsClick}>
            {showDetails ? "닫기" : "상세보기"}
          </button>
        </p>
      </div>
      {showDetails && (
        <div>
          <Table  page={2}/>
          <div className="btnbox">
            <button className="agree" onClick={handleApprovalClick}>
              승인
            </button>
            <button className="reject" onClick={handleRejectionClick}>
              반려
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default ShopReservationList;

